-        checked="checked"
-      {% endif %}
-      {% if option_disabled %}
-        data-disabled=""
-      {% endif %}
+      {% if option.selected_value == value %} checked="checked"{% endif %}
+      {% if option_disabled %} data-disabled=""{% endif %}
       value="{{ value | escape }}"
       name="{{ option.name }}"
       id="{{ id }}"
       data-index="option{{ option.position }}"
     >
+
     <label
