+<style>
+  .custom-variant-box {
+    border: 1px solid #ccc;
+    border-radius: 8px;
+    padding: 1rem;
+    text-align: center;
+    cursor: pointer;
+    display: inline-block;
+    min-width: 100px;
+    margin: 0.5rem;
+    transition: border-color 0.3s, box-shadow 0.3s;
+  }
+
+  input[type="radio"]:checked + .custom-variant-box {
+    border-color: #006341;
+    box-shadow: 0 0 0 2px rgba(0, 99, 65, 0.2);
+    background-color: #f4faf7;
+    font-weight: bold;
+  }
+
+  .custom-variant-content {
+    display: flex;
+    flex-direction: column;
+    align-items: center;
+  }
+
+  .custom-variant-size {
